import base64

# Replace the string below with the Base64 string from your response
qr_base64 = ""

# Decode the Base64 string
img_data = base64.b64decode(qr_base64)

# Write the binary data to a PNG file
with open("booking.png", "wb") as f:
    f.write(img_data)

print("QR code saved as booking_qr.png")
